package Arithmeticperators;

import java.util.Scanner;

public class Sumndifference {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int n1=s.nextInt();
		System.out.println(n+n1);
		System.out.println(n-n1);
		System.out.println(n*n1);
		System.out.println(n/n1);
		System.out.println(n%n1);
	
	}

}
